import React, { useState } from "react";
import MapSection from "./MapSection";
import "./OrderNow.css";

const providers = [
  { value: "nurse", label: "ممرض" },
  { value: "doctor", label: "دكتور" },
  { value: "pharmacist", label: "صيدلي" },
  { value: "physio", label: "علاج طبيعي" },
  { value: "tech", label: "فني" },
  { value: "other", label: "أخرى" },
];

const services = [
  "قياس الضغط", "قياس السكر", "حقن", "علاج طبيعي", "استشارة",
  "تركيب كانيولا", "تعليق محاليل", "تغيير جبس", "خياطة جروح", "تغيير قسطرة",
  "سحب عينات", "رعاية جرح", "علاج حروق", "إسعافات أولية", "رعاية كبار السن",
  "رعاية أطفال", "زيارات منزلية", "علاج جلسات", "إزالة غرز", "تركيب أنبوب تغذية",
  "خدمة أخرى"
];

export default function OrderNow() {
  const [selectedProvider, setSelectedProvider] = useState("nurse");
  const [selectedServices, setSelectedServices] = useState([]);
  const [location, setLocation] = useState(null);
  const [searching, setSearching] = useState(false);

  const handleGeoLocate = () => {
    if (!navigator.geolocation) {
      alert("المتصفح لا يدعم تحديد الموقع");
      return;
    }
    navigator.geolocation.getCurrentPosition(
      (pos) => setLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      () => alert("تعذر تحديد الموقع!"),
    );
  };

  const handleServiceChange = (service) => {
    setSelectedServices((prev) =>
      prev.includes(service)
        ? prev.filter((s) => s !== service)
        : [...prev, service]
    );
  };

  // بعد تحديد الموقع يظهر "جاري البحث"
  React.useEffect(() => {
    if (location) {
      setSearching(true);
      const t = setTimeout(() => setSearching(false), 2500);
      return () => clearTimeout(t);
    }
  }, [location]);

  return (
    <div className="order-now-container">
      <div className="map-section-large">
        <MapSection location={location} searching={searching} />
      </div>
      <div className="order-form">
        <h2>حجز مقدم خدمة طبية</h2>
        <label className="label">اختر نوع مقدم الخدمة:</label>
        <select
          className="select"
          value={selectedProvider}
          onChange={(e) => setSelectedProvider(e.target.value)}
        >
          {providers.map((provider) => (
            <option key={provider.value} value={provider.value}>
              {provider.label}
            </option>
          ))}
        </select>
        <label className="label" style={{ marginTop: 14 }}>
          اختر الخدمات المطلوبة:
        </label>
        <div className="services-list">
          {services.map((service, i) => (
            <label key={service} className="service-checkbox">
              <input
                type="checkbox"
                checked={selectedServices.includes(service)}
                onChange={() => handleServiceChange(service)}
              />
              {service}
            </label>
          ))}
        </div>
        <button className="location-btn" onClick={handleGeoLocate}>
          حدد موقعي على الخريطة
        </button>
        <div className="location-status">
          {location
            ? <span style={{color: "#38b2ac"}}>تم تحديد موقعك بنجاح!</span>
            : "لم يتم تحديد الموقع بعد"}
        </div>
      </div>
    </div>
  );
}
