import React, { useState, useEffect } from "react";
import "./OrderNow.css";

const providerTypes = [
  { key: "doctor", label: "ÿ∑ÿ®Ÿäÿ®", icon: "/doctor-icon.png", color: "#3182ce" },
  { key: "nurse", label: "ŸÖŸÖÿ±ÿ∂", icon: "/nurse-icon.png", color: "#38b2ac" },
  { key: "physio", label: "ÿπŸÑÿßÿ¨ ÿ∑ÿ®ŸäÿπŸä", icon: "/pharmacist-icon.png", color: "#f6ad55" },
  { key: "lab", label: "ÿ™ÿ≠ÿßŸÑŸäŸÑ", icon: "/pharmacist-icon.png", color: "#f56565" },
  { key: "xray", label: "ÿ£ÿ¥ÿπÿ©", icon: "/pharmacist-icon.png", color: "#805ad5" },
  { key: "pharmacist", label: "ÿµŸäÿØŸÑŸä", icon: "/pharmacist-icon.png", color: "#ecc94b" },
];

export default function OrderNow({ user }) {
  const [step, setStep] = useState(0);
  const [selectedType, setSelectedType] = useState(null);
  const [specialties, setSpecialties] = useState([]);
  const [selectedSpecialty, setSelectedSpecialty] = useState("");
  const [services, setServices] = useState([]);
  // ÿØÿπŸÖ ÿßÿÆÿ™Ÿäÿßÿ± ÿ£ŸÉÿ´ÿ± ŸÖŸÜ ÿÆÿØŸÖÿ©
  const [selectedServices, setSelectedServices] = useState([]);
  const [location, setLocation] = useState({ address: "", lat: "", lng: "" });
  const [basePrice, setBasePrice] = useState(0);
  const [suggestedPrice, setSuggestedPrice] = useState("");
  const [sending, setSending] = useState(false);
  // ŸÖÿ™ÿ∫Ÿäÿ± ŸÑÿ™ÿ≠ÿØŸäÿØ ÿ¢ÿÆÿ± ÿπŸÜÿµÿ± ŸÖÿÆÿ™ÿßÿ± (ŸÑÿ≤ÿ± ÿßŸÑÿ™ÿßŸÑŸä ÿßŸÑÿπÿµÿ±Ÿä)
  const [lastSelectedIndex, setLastSelectedIndex] = useState(null);
  // Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ŸáŸÜÿß ŸàŸÑŸäÿ≥ ÿ®ÿπÿØ ÿßŸÑÿ¥ÿ±Ÿàÿ∑
  const [showSuccess, setShowSuccess] = useState(false);

  // ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿÆÿµÿµÿßÿ™ ŸÖŸÜ db.json
  useEffect(() => {
    if (selectedType) {
      fetch(`http://localhost:5000/services?providerType=${selectedType.key}`)
        .then(res => res.json())
        .then(data => {
          // ÿßÿ≥ÿ™ÿÆÿ±ÿ¨ ÿßŸÑÿ™ÿÆÿµÿµÿßÿ™ ÿßŸÑŸÅÿ±ŸäÿØÿ©
          const specialties = [...new Set(data.map(s => s.specialty))];
          setSpecialties(specialties);
        });
    }
  }, [selectedType]);

  // ÿ¨ŸÑÿ® ÿßŸÑÿÆÿØŸÖÿßÿ™ ÿ≠ÿ≥ÿ® ŸÜŸàÿπ ŸÖŸÇÿØŸÖ ÿßŸÑÿÆÿØŸÖÿ©
  useEffect(() => {
    if (selectedType) {
      let endpoint = "";
      if (selectedType.key === "nurse") endpoint = "/nurseServices";
      else if (selectedType.key === "doctor") endpoint = "/doctorServices";
      else if (selectedType.key === "physio") endpoint = "/physioServices";
      else if (selectedType.key === "pharmacist") endpoint = "/pharmacistServices";
      else if (selectedType.key === "lab") endpoint = "/labServices";
      else if (selectedType.key === "xray") endpoint = "/xrayServices";
      if (endpoint) {
        fetch(`http://localhost:5000${endpoint}`)
          .then(res => res.json())
          .then(data => setServices(data || []));
      } else {
        setServices([]);
      }
    }
  }, [selectedType]);

  // ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ≥ÿπÿ± ÿßŸÑŸÉŸÑŸä ŸÑÿ¨ŸÖŸäÿπ ÿßŸÑÿÆÿØŸÖÿßÿ™ ÿßŸÑŸÖÿÆÿ™ÿßÿ±ÿ©
  useEffect(() => {
    if (selectedType && selectedServices.length > 0) {
      const total = selectedServices.reduce((sum, name) => {
        const service = services.find(s => s.name === name);
        return sum + (service ? service.basePrice : 0);
      }, 0);
      setBasePrice(total);
    } else {
      setBasePrice(0);
    }
  }, [selectedServices, services, selectedType]);

  // ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑŸÖŸàŸÇÿπ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã
  function detectLocation() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(pos => {
        setLocation({
          ...location,
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      });
    }
  }

  // ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ∑ŸÑÿ® (ŸäÿØÿπŸÖ ÿπÿØÿ© ÿÆÿØŸÖÿßÿ™)
  // ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ∑ŸÑÿ® (ŸäÿØÿπŸÖ ÿπÿØÿ© ÿÆÿØŸÖÿßÿ™) ŸÖÿπ ÿ±ÿ≥ÿßŸÑÿ© ŸÜÿ¨ÿßÿ≠ Ÿàÿ™Ÿàÿ¨ŸäŸá ÿ™ŸÑŸÇÿßÿ¶Ÿä
  // ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ∑ŸÑÿ® (Ÿäÿ±ÿ®ÿ∑ ÿßŸÑÿ∑ŸÑÿ® ÿ®ŸÖŸÇÿØŸÖ ÿÆÿØŸÖÿ© ŸÖÿ™ÿßÿ≠)
  function handleSubmit(e) {
    e.preventDefault();
    setSending(true);
    // ÿ¨ŸÑÿ® ŸÖŸÇÿØŸÖŸä ÿßŸÑÿÆÿØŸÖÿ© ÿßŸÑŸÖÿ™ÿßÿ≠ŸäŸÜ ŸÑŸáÿ∞ÿß ÿßŸÑÿ™ÿÆÿµÿµ
    fetch(`http://localhost:5000/users?type=provider&providerType=${encodeURIComponent(selectedType.label)}`)
      .then(res => res.json())
      .then(providers => {
        // ÿßÿÆÿ™ÿ± ÿ£ŸàŸÑ ŸÖŸÇÿØŸÖ ÿÆÿØŸÖÿ© ŸÖÿ™ÿßÿ≠ (ŸäŸÖŸÉŸÜŸÉ ÿ™ÿ∑ŸàŸäÿ± ÿßŸÑŸÖŸÜÿ∑ŸÇ ŸÑÿßÿ≠ŸÇÿßŸã)
        const provider = providers[0];
        const orderBody = {
          patientId: user.id,
          providerType: selectedType.key,
          specialty: selectedSpecialty,
          serviceNames: selectedServices,
          location,
          basePrice,
          suggestedPrice,
          status: "new",
          createdAt: new Date().toISOString(),
          providerId: provider ? provider.id : null,
          providerName: provider ? provider.fullName : null,
          providerPhone: provider ? provider.phone : null,
          patientName: user.fullName,
          address: location.address
        };
        return fetch("http://localhost:5000/orders", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(orderBody),
        });
      })
      .then(() => {
        setSending(false);
        setShowSuccess(true);
        setTimeout(() => {
          setShowSuccess(false);
          if (window && window.location) {
            window.location.href = "/track-order";
          }
        }, 1500);
      });
  }

  // Ÿàÿßÿ¨Ÿáÿ© ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿ™ÿÆÿµÿµ ÿ£Ÿà ÿßŸÑÿÆÿØŸÖÿ©
  if (step === 0) {
    return (
      <div className="order-cards-container">
        <h2>ÿßÿÆÿ™ÿ± ŸÜŸàÿπ ŸÖŸÇÿØŸÖ ÿßŸÑÿÆÿØŸÖÿ©</h2>
        <div className="cards-list">
          {providerTypes.map((type) => (
            <div
              key={type.key}
              className="order-card"
              style={{ background: type.color }}
              onClick={() => {
                setSelectedType(type);
                setStep(1);
              }}
            >
              <img src={type.icon} alt={type.label} className="order-card-icon" />
              <div>{type.label}</div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // Ÿàÿßÿ¨Ÿáÿ© ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿÆÿØŸÖÿßÿ™ (ÿØÿπŸÖ multi-select + ÿ≤ÿ± "ÿßŸÑÿ™ÿßŸÑŸä" ÿ£ÿ≥ŸÅŸÑ ÿ¢ÿÆÿ± ÿÆÿØŸÖÿ© ŸÖÿÆÿ™ÿßÿ±ÿ© ÿ®ÿ¥ŸÉŸÑ ÿπÿµÿ±Ÿä)
  if (step === 1 && selectedType) {
    return (
      <div className="order-step">
        <h2>ÿßÿÆÿ™ÿ± ÿßŸÑÿÆÿØŸÖÿßÿ™ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©</h2>
        <div className="services-list-grid-modern">
          {services.length === 0 && <div className="no-services">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿÆÿØŸÖÿßÿ™ ŸÖÿ™ÿßÿ≠ÿ© ŸÑŸáÿ∞ÿß ÿßŸÑÿ™ÿÆÿµÿµ ÿ≠ÿßŸÑŸäÿßŸã.</div>}
          {services.map((s, idx) => {
            const isSelected = selectedServices.includes(s.name);
            return (
              <div
                key={s.id}
                className={`service-card-modern${isSelected ? " selected" : ""}`}
                onClick={() => {
                  setSelectedServices(prev => {
                    let next;
                    if (isSelected) {
                      next = prev.filter(name => name !== s.name);
                    } else {
                      next = [...prev, s.name];
                      setLastSelectedIndex(idx);
                    }
                    // ÿ•ÿ∞ÿß ÿ£ŸÑÿ∫Ÿâ ÿ¢ÿÆÿ± ÿÆÿØŸÖÿ© ŸÖÿÆÿ™ÿßÿ±ÿ©ÿå ÿ≠ÿØÿ´ ÿßŸÑŸÖÿ§ÿ¥ÿ±
                    if (isSelected && lastSelectedIndex === idx) {
                      setLastSelectedIndex(null);
                    }
                    return next;
                  });
                }}
              >
                <div className="service-icon-modern">
                  <span role="img" aria-label="ÿÆÿØŸÖÿ©">ü©∫</span>
                </div>
                <div className="service-info-modern">
                  <div className="service-name-modern">{s.name}</div>
                  <div className="service-price-modern">{s.basePrice} ÿ¨.ŸÖ</div>
                </div>
                {/* ÿ≤ÿ± ÿßŸÑÿ™ÿßŸÑŸä Ÿäÿ∏Ÿáÿ± ÿ£ÿ≥ŸÅŸÑ ÿ¢ÿÆÿ± ÿÆÿØŸÖÿ© ŸÖÿÆÿ™ÿßÿ±ÿ© */}
                {selectedServices.length > 0 && lastSelectedIndex === idx && (
                  <button
                    className="next-btn-modern floating-next-btn"
                    onClick={e => {
                      e.stopPropagation();
                      setStep(3);
                    }}
                  >
                    ÿßŸÑÿ™ÿßŸÑŸä
                  </button>
                )}
              </div>
            );
          })}
        </div>
      </div>
    );
  }



  // Ÿàÿßÿ¨Ÿáÿ© ÿßŸÑŸÖŸàŸÇÿπ ŸàÿßŸÑÿ≥ÿπÿ±
  if (step === 3 && selectedType) {
    return (
      <>
        {showSuccess && (
          <div className="success-message-green modern-success">
            <span className="success-icon">‚úî</span>
            ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ∑ŸÑÿ® ÿ®ŸÜÿ¨ÿßÿ≠! ÿ≥Ÿäÿ™ŸÖ ÿ™ÿ≠ŸàŸäŸÑŸÉ ŸÑÿµŸÅÿ≠ÿ© ÿ™ÿ™ÿ®ÿπ ÿßŸÑÿ∑ŸÑÿ®...
          </div>
        )}
        <form className="order-step" onSubmit={handleSubmit}>
          <h2>ÿ≠ÿØÿØ ŸÖŸàŸÇÿπŸÉ</h2>
          <div className="location-row-modern">
            <input
              type="text"
              className="modern-input"
              placeholder="ÿßŸÑÿπŸÜŸàÿßŸÜ ÿ®ÿßŸÑÿ™ŸÅÿµŸäŸÑ"
              value={location.address}
              onChange={e => setLocation({ ...location, address: e.target.value })}
            />
            <button type="button" className="detect-btn-modern" onClick={detectLocation}>
              <span role="img" aria-label="ŸÖŸàŸÇÿπ">üìç</span> ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑŸÖŸàŸÇÿπ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã
            </button>
          </div>
          <div className="price-box modern-price-box">
            <b>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿ≥ÿπÿ± ÿßŸÑÿÆÿØŸÖÿßÿ™:</b> <span className="main-price">{basePrice} ÿ¨.ŸÖ</span>
          </div>
          <input
            type="number"
            className="modern-input"
            placeholder="ÿßŸÇÿ™ÿ±ÿßÿ≠ ÿ≥ÿπÿ± (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
            value={suggestedPrice}
            onChange={e => setSuggestedPrice(e.target.value)}
          />
          <button type="submit" disabled={sending} className="modern-send-btn">
            {sending ? <span className="loader"></span> : <><span role="img" aria-label="ÿ•ÿ±ÿ≥ÿßŸÑ">üöÄ</span> ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ∑ŸÑÿ®</>}
          </button>
        </form>
      </>
    );
  }

  return null;
}
