import React, { useEffect, useState } from "react";
import MapSection from "./MapSection";
import "./TrackOrder.css";

// يفترض أن يتم تمرير orderId كـ prop أو من خلال route params
export default function TrackOrder({ orderId = "a435" }) {
  const [order, setOrder] = useState(null);
  const [status, setStatus] = useState("جاري تجهيز الطلب...");

  useEffect(() => {
    // جلب بيانات الطلب من قاعدة البيانات
    fetch("/db.json")
      .then(res => res.json())
      .then(data => {
        const found = (data.orders || []).find(o => o.id === orderId);
        setOrder(found);
        if (found) setStatus(found.status || "جاري تجهيز الطلب...");
      });
  }, [orderId]);

  return (
    <div className="track-main">
      <h2>متابعة الطلب</h2>
      <div className="track-status">{status}</div>
      {order && (
        <div className="order-prices-box">
          <div><b>سعر الخدمة الأساسي:</b> <span className="main-price">{order.basePrice} ج.م</span></div>
          {order.suggestedPrice && order.suggestedPrice !== order.basePrice?.toString() && (
            <div><b>السعر المقترح من المريض:</b> <span className="suggested-price">{order.suggestedPrice} ج.م</span></div>
          )}
        </div>
      )}
      <div className="track-map">
        <MapSection location={order?.location || {lat: 30.0444, lng: 31.2357}} searching={false} />
      </div>
    </div>
  );
}
