import React, { useState } from "react";
import "./OrderNow.css";

export default function OrderNow() {
  const [form, setForm] = useState({
    patientName: "",
    location: "",
    phone: "",
    services: [],
  });
  const [sending, setSending] = useState(false);
  const [done, setDone] = useState(false);

  const availableServices = [
    "قياس الضغط",
    "حقن",
    "تعليق محاليل",
    "تركيب كانيولا",
    "رعاية جرح",
  ];

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleServiceChange(service) {
    setForm((prev) => {
      const exists = prev.services.includes(service);
      if (exists) {
        return { ...prev, services: prev.services.filter((s) => s !== service) };
      } else {
        return { ...prev, services: [...prev.services, service] };
      }
    });
  }

  function handleSubmit(e) {
    e.preventDefault();
    setSending(true);
    fetch("http://localhost:4000/api/request", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    })
      .then((res) => res.json())
      .then(() => {
        setSending(false);
        setDone(true);
        setForm({
          patientName: "",
          location: "",
          phone: "",
          services: [],
        });
      })
      .catch(() => setSending(false));
  }

  if (done)
    return (
      <div className="order-done-msg">
        ✅ تم إرسال الطلب بنجاح!<br />
        سيتم التواصل مع أقرب مقدم رعاية صحي متاح.
        <br />
        <button onClick={() => setDone(false)}>طلب جديد</button>
      </div>
    );

  return (
    <div className="order-now-container">
      <form className="order-form" onSubmit={handleSubmit}>
        <h2>احجز مقدم رعاية صحية</h2>

        <label className="label">اسمك</label>
        <input
          type="text"
          name="patientName"
          value={form.patientName}
          onChange={handleChange}
          required
        />

        <label className="label">العنوان أو الموقع</label>
        <input
          type="text"
          name="location"
          value={form.location}
          onChange={handleChange}
          required
        />

        <label className="label">رقم التليفون</label>
        <input
          type="text"
          name="phone"
          value={form.phone}
          onChange={handleChange}
          required
        />

        <label className="label">الخدمات المطلوبة</label>
        <div className="services-list">
          {availableServices.map((service) => (
            <label className="service-checkbox" key={service}>
              <input
                type="checkbox"
                checked={form.services.includes(service)}
                onChange={() => handleServiceChange(service)}
              />
              {service}
            </label>
          ))}
        </div>

        <button
          className="location-btn"
          type="submit"
          disabled={sending || form.services.length === 0}
        >
          {sending ? "جاري الإرسال..." : "إرسال الطلب"}
        </button>
      </form>
    </div>
  );
}
