import React, { useState } from "react";
import "./App.css";

const providers = [
  { value: "nurse", label: "ممرض" },
  { value: "doctor", label: "دكتور" },
  { value: "pharmacist", label: "صيدلي" },
  { value: "physio", label: "أخصائي علاج طبيعي" },
  { value: "tech", label: "فني طبي" },
  { value: "other", label: "أخرى" },
];

const services = [
  "قياس الضغط",
  "قياس السكر",
  "حقن",
  "علاج طبيعي",
  "استشارة",
  "خدمة أخرى"
];

export default function App() {
  const [selectedProvider, setSelectedProvider] = useState("nurse");
  const [selectedServices, setSelectedServices] = useState([]);
  const [location, setLocation] = useState(null);

  // خريطة Leaflet تظهر إذا كان فيه موقع
  const renderMap = () => {
    if (!location) return <div style={{ textAlign: "center", color: "#888" }}>لم يتم تحديد الموقع بعد</div>;
    return (
      <iframe
        title="خريطة المريض"
        width="100%"
        height="300"
        style={{ borderRadius: "16px", marginTop: "12px" }}
        frameBorder="0"
        src={`https://maps.google.com/maps?q=${location.lat},${location.lng}&z=15&output=embed`}
        allowFullScreen
      ></iframe>
    );
  };

  const handleGeoLocate = () => {
    if (!navigator.geolocation) {
      alert("المتصفح لا يدعم تحديد الموقع");
      return;
    }
    navigator.geolocation.getCurrentPosition(
      (pos) => setLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      () => alert("تعذر تحديد الموقع!"),
    );
  };

  const handleServiceChange = (service) => {
    setSelectedServices((prev) =>
      prev.includes(service)
        ? prev.filter((s) => s !== service)
        : [...prev, service]
    );
  };

  return (
    <div className="main-wrapper">
      <header className="header">
        <h2>حجز مقدم خدمة طبية</h2>
      </header>
      <div className="content-container">
        <div className="form-section">
          <label className="label">اختر نوع مقدم الخدمة:</label>
          <select
            className="select"
            value={selectedProvider}
            onChange={(e) => setSelectedProvider(e.target.value)}
          >
            {providers.map((provider) => (
              <option key={provider.value} value={provider.value}>
                {provider.label}
              </option>
            ))}
          </select>
          <label className="label" style={{ marginTop: 16 }}>
            اختر الخدمات المطلوبة:
          </label>
          <div className="services-list">
            {services.map((service) => (
              <label key={service} className="service-checkbox">
                <input
                  type="checkbox"
                  checked={selectedServices.includes(service)}
                  onChange={() => handleServiceChange(service)}
                />
                {service}
              </label>
            ))}
          </div>
          <button className="location-btn" onClick={handleGeoLocate}>
            حدد موقعي على الخريطة
          </button>
          {renderMap()}
        </div>
        <div className="side-info">
          <h3>فكرة التطبيق:</h3>
          <p>
            يتيح لك هذا التطبيق اختيار مقدم الخدمة الطبية الأقرب لموقعك الجغرافي
            (ممرض - دكتور - صيدلي - فني - علاج طبيعي - ...)،
            وتحديد الخدمات المطلوبة بسهولة.
          </p>
        </div>
      </div>
    </div>
  );
}
