import React, { useState, useEffect } from "react";
import "./CareProviderDashboard.css";
import AdminPage from "../AdminPage";

export default function CareProviderDashboard({ user, setUser }) {
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [orders, setOrders] = useState([]);
  const [showAdmin, setShowAdmin] = useState(false);
  const [popupMsg, setPopupMsg] = useState("");

  // ÿ¨ŸÑÿ® ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ŸÖŸÜ db.json
  useEffect(() => {
    // ÿ¨ŸÑÿ® ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÖÿÆÿµÿµÿ© ŸÑŸáÿ∞ÿß ÿßŸÑŸÖŸÇÿØŸÖ ŸÅŸÇÿ∑ (providerId)
    fetch(`http://localhost:5000/orders?providerId=${user.id}`)
      .then(res => res.json())
      .then(setOrders);
  }, [user.id]);

  // ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿ≠ÿ≥ÿ® ÿßŸÑÿ≠ÿßŸÑÿ©
  const ordersByStatus = (status) =>
    orders.filter((o) => o.status === status);

  // ÿπÿØÿØ ÿßŸÑÿÆÿØŸÖÿßÿ™ ÿßŸÑŸÖŸÜÿ¨ÿ≤ÿ© (ÿ™ÿ¨ÿ±Ÿäÿ®Ÿä)
  const completedCount = ordersByStatus("done").length;

  // ÿØÿßŸÑÿ© ŸÑŸÇÿ®ŸàŸÑ ÿ£Ÿà ÿ±ŸÅÿ∂ ÿßŸÑÿ∑ŸÑÿ®
  function handleOrderAction(orderId, action) {
    const order = orders.find(o => o.id === orderId);
    if (!order) return;
    // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ∑ŸÑÿ® ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
    fetch(`http://localhost:5000/orders/${orderId}`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ status: action === "accept" ? "accepted" : "rejected" })
    })
      .then(() => {
        setOrders(prev => prev.map(o => o.id === orderId ? { ...o, status: action === "accept" ? "accepted" : "rejected" } : o));
        setSelectedOrder(null);
        setPopupMsg(action === "accept" ? `ÿ™ŸÖ ŸÇÿ®ŸàŸÑ ÿ∑ŸÑÿ® ${order.patientName} ÿ®ŸÜÿ¨ÿßÿ≠` : `ÿ™ŸÖ ÿ±ŸÅÿ∂ ÿ∑ŸÑÿ® ${order.patientName}`);
        setTimeout(() => setPopupMsg(""), 2000);
      });
  }

  return (
    <div className="provider-dashboard">
      <header>
        <h2>ŸÖÿ±ÿ≠ÿ®ÿßŸã {user.fullName || user.name} üëã</h2>
        <button className="logout-btn" onClick={() => setUser(null)}>
          ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
        </button>
        <button className="admin-access-btn" style={{marginRight: 12}} onClick={() => setShowAdmin(true)}>
          ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ£ÿ≥ÿπÿßÿ±
        </button>
      </header>
      <div className="provider-profile">
        <div>
          <b>ÿßŸÑÿ™ÿÆÿµÿµ:</b> {user.providerType}
        </div>
        <div>
          <b>ÿßŸÑÿßÿ≥ŸÖ:</b> {user.fullName || user.name}
        </div>
        <div>
          <b>ÿπÿØÿØ ÿßŸÑÿÆÿØŸÖÿßÿ™ ÿßŸÑŸÖŸÜÿ¨ÿ≤ÿ©:</b> {completedCount}
        </div>
      </div>
      <div className="provider-content">
        <div className="provider-card" onClick={() => {}}>
          <h3>ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑÿ¨ÿØŸäÿØÿ©</h3>
          <div className="orders-list">
            {ordersByStatus("new").length === 0 && <div className="empty-state">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ∑ŸÑÿ®ÿßÿ™ ÿ¨ÿØŸäÿØÿ©</div>}
            {ordersByStatus("new").map((order) => (
              <div key={order.id} className="order-item" onClick={() => setSelectedOrder(order)}>
                <b>{order.patientName}</b> - {order.serviceNames ? order.serviceNames.join(", ") : order.service}
              </div>
            ))}
          </div>
        </div>
        <div className="provider-card">
          <h3>ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÖŸÇÿ®ŸàŸÑÿ© (ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ŸÜÿ¨ÿßÿ≤Ÿáÿß)</h3>
          <div className="orders-list">
            {ordersByStatus("accepted").length === 0 && <div className="empty-state">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ∑ŸÑÿ®ÿßÿ™</div>}
            {ordersByStatus("accepted").map((order) => (
              <div key={order.id} className="order-item">
                <b>{order.patientName}</b> - {order.serviceNames ? order.serviceNames.join(", ") : order.service}
              </div>
            ))}
          </div>
        </div>
        <div className="provider-card">
          <h3>ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÖŸÇÿ®ŸàŸÑÿ© (ÿ™ŸÖ ÿ•ŸÜÿ¨ÿßÿ≤Ÿáÿß)</h3>
          <div className="orders-list">
            {ordersByStatus("done").length === 0 && <div className="empty-state">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ∑ŸÑÿ®ÿßÿ™</div>}
            {ordersByStatus("done").map((order) => (
              <div key={order.id} className="order-item">
                <b>{order.patientName}</b> - {order.serviceNames ? order.serviceNames.join(", ") : order.service}
              </div>
            ))}
          </div>
        </div>
        <div className="provider-card">
          <h3>ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÖÿ±ŸÅŸàÿ∂ÿ©</h3>
          <div className="orders-list">
            {ordersByStatus("rejected").length === 0 && <div className="empty-state">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ∑ŸÑÿ®ÿßÿ™</div>}
            {ordersByStatus("rejected").map((order) => (
              <div key={order.id} className="order-item">
                <b>{order.patientName}</b> - {order.serviceNames ? order.serviceNames.join(", ") : order.service}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Popup ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ∑ŸÑÿ® */}
      {selectedOrder && (
        <div className="order-popup-overlay" onClick={() => setSelectedOrder(null)}>
          <div className="order-popup" onClick={e => e.stopPropagation()}>
            <h3>ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ∑ŸÑÿ®</h3>
            <div><b>ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ±Ÿäÿ∂:</b> {selectedOrder.patientName}</div>
            <div><b>ÿßŸÑÿπŸÜŸàÿßŸÜ:</b> {selectedOrder.address}</div>
            <div><b>ÿßŸÑÿÆÿØŸÖÿßÿ™ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©:</b> {selectedOrder.serviceNames ? selectedOrder.serviceNames.join(", ") : selectedOrder.service}</div>
            <div><b>ÿßŸÑÿ≥ÿπÿ± ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿä:</b> {selectedOrder.basePrice} ÿ¨.ŸÖ</div>
            <div><b>ÿßŸÑÿ≥ÿπÿ± ÿßŸÑŸÖŸÇÿ™ÿ±ÿ≠ ŸÖŸÜ ÿßŸÑŸÖÿ±Ÿäÿ∂:</b> {selectedOrder.suggestedPrice} ÿ¨.ŸÖ</div>
            <div style={{margin: "10px 0"}}>
              <iframe
                title="ÿÆÿ±Ÿäÿ∑ÿ© ÿßŸÑŸÖÿ±Ÿäÿ∂"
                width="100%"
                height="180"
                style={{ borderRadius: "12px" }}
                frameBorder="0"
                src={`https://maps.google.com/maps?q=${selectedOrder.location.lat},${selectedOrder.location.lng}&z=15&output=embed`}
                allowFullScreen
              ></iframe>
            </div>
            <div style={{display: "flex", gap: 10, marginTop: 10}}>
              {selectedOrder.status === "new" && <>
                <button className="accept-btn" onClick={() => handleOrderAction(selectedOrder.id, "accept")}>ŸÇÿ®ŸàŸÑ ÿßŸÑÿ∑ŸÑÿ®</button>
                <button className="reject-btn" onClick={() => handleOrderAction(selectedOrder.id, "reject")}>ÿ±ŸÅÿ∂ ÿßŸÑÿ∑ŸÑÿ®</button>
              </>}
            </div>
          </div>
        </div>
      )}
      {/* Popup ÿ±ÿ≥ÿßŸÑÿ© ŸÇÿ®ŸàŸÑ/ÿ±ŸÅÿ∂ */}
      {popupMsg && (
        <div className="provider-popup-msg">{popupMsg}</div>
      )}
      {/* ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ£ÿ≥ÿπÿßÿ± */}
      {showAdmin && (
        <div className="order-popup-overlay" onClick={() => setShowAdmin(false)}>
          <div className="order-popup" onClick={e => e.stopPropagation()}>
            <AdminPage />
            <button className="close-popup-btn" onClick={() => setShowAdmin(false)}>ÿ•ÿ∫ŸÑÿßŸÇ</button>
          </div>
        </div>
      )}
    </div>
  );
}
