import React, { useState, useEffect } from "react";
import { ThemeProvider, useTheme } from "./components/ThemeContext";
import Navbar from "./components/Navbar";
import FallingIcons from "./components/FallingIcons";
import Profile from "./components/Profile";
import OrderNow from "./components/OrderNow";
import TrackOrder from "./components/TrackOrder";
import Settings from "./components/Settings";
import LoginRegister from "./components/LoginRegister";
import CareProviderDashboard from "./components/CareProviderDashboard";
import AdminPage from "./AdminPage";
import AdminServicesPage from "./AdminServicesPage";
import "./App.css";

// ÿßÿ≥ÿ™ÿÆÿØŸÖ Ÿáÿ∞ÿß ÿßŸÑŸÖÿ™ÿ∫Ÿäÿ± ŸÅŸä ŸÉŸÑ fetch ÿ®ÿØŸÑÿßŸã ŸÖŸÜ ŸÉÿ™ÿßÿ®ÿ© ÿßŸÑÿ±ÿßÿ®ÿ∑ ŸÖÿ®ÿßÿ¥ÿ±ÿ©
const API_BASE =
  window.location.hostname === "localhost"
    ? "http://localhost:5000"
    : "https://helthend-production.up.railway.app";

function GlobalNotificationPopup({ user }) {
  const [notification, setNotification] = useState(null);
  const [show, setShow] = useState(false);

  useEffect(() => {
    if (!user) return;
    // ÿ¨ŸÑÿ® ÿ¢ÿÆÿ± ÿ•ÿ¥ÿπÿßÿ± ŸÖŸàÿ¨Ÿá ŸÑŸÑŸÅÿ¶ÿ© ÿßŸÑŸÖŸÜÿßÿ≥ÿ®ÿ©
    fetch(`${API_BASE}/notifications?target=${user.type === 'patient' ? 'patients' : 'providers'}&_sort=date&_order=desc&_limit=1`)
      .then(res => res.json())
      .then(data => {
        if (data.length > 0) {
          setNotification(data[0]);
          setShow(true);
        }
      });
  }, [user]);

  if (!notification || !show) return null;

  // ÿØÿπŸÖ ÿßŸÑÿ±Ÿàÿßÿ®ÿ∑ ŸÅŸä ŸÜÿµ ÿßŸÑÿ•ÿ¥ÿπÿßÿ± (ŸÖÿ´ÿßŸÑ: [ÿ±ÿßÿ®ÿ∑](https://example.com))
  function renderMessage(msg) {
    if (!msg || typeof msg !== "string") return null;
    return msg.split(/(\[.*?\]\(.*?\))/g).map((part, i) => {
      const match = part.match(/\[(.*?)\]\((.*?)\)/);
      if (match) {
        return <a key={i} href={match[2]} target="_blank" rel="noopener noreferrer" style={{color:'#3182ce',textDecoration:'underline'}}>{match[1]}</a>;
      }
      return part;
    });
  }

  // ÿØÿπŸÖ body ÿ•ÿ∞ÿß ŸÑŸÖ ŸäŸàÿ¨ÿØ message
  const notifText = notification.message || notification.body || "";

  return (
    <div className="popup-overlay" style={{zIndex: 10000}}>
      <div className="popup-card" style={{maxWidth:400,position:'relative'}}>
        <button onClick={()=>setShow(false)} style={{position:'absolute',top:8,left:8,background:'none',border:'none',fontSize:'1.5em',color:'#888',cursor:'pointer'}}>√ó</button>
        <h3 style={{color:'#3182ce'}}>ÿ•ÿ¥ÿπÿßÿ± ŸáÿßŸÖ</h3>
        <div style={{margin:'12px 0',fontSize:'1.1em'}}>{renderMessage(notifText)}</div>
        <div style={{fontSize:'0.9em',color:'#888'}}>ÿ®ÿ™ÿßÿ±ŸäÿÆ: {new Date(notification.date || notification.createdAt).toLocaleString()}</div>
      </div>
    </div>
  );
}

function AppContent() {
  // ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿµÿßÿØŸÇÿ© ŸÖÿπ ÿ≠ŸÅÿ∏ ÿßŸÑÿ¨ŸÑÿ≥ÿ©
  const [user, setUserState] = useState(() => {
    try {
      const u = localStorage.getItem("user");
      return u ? JSON.parse(u) : null;
    } catch {
      return null;
    }
  });
