import React, { useState } from "react";
import "./AdminServices.css";

const PASSWORD = "2%txPg6DXN";
const TABS = [
  { key: "nurseServices", label: "خدمات التمريض" },
  { key: "doctorServices", label: "خدمات الطبيب" },
  { key: "physioServices", label: "خدمات العلاج الطبيعي" },
];

const AdminServices = ({ allServices, onUpdate }) => {
  const [password, setPassword] = useState("");
  const [authenticated, setAuthenticated] = useState(false);
  const [tab, setTab] = useState("nurseServices");
  const [edited, setEdited] = useState({
    nurseServices: allServices.nurseServices,
    doctorServices: allServices.doctorServices,
    physioServices: allServices.physioServices,
  });

  const handleLogin = (e) => {
    e.preventDefault();
    if (password === PASSWORD) setAuthenticated(true);
    else alert("كلمة المرور غير صحيحة");
  };

  const handleChange = (serviceType, id, value) => {
    setEdited((prev) => ({
      ...prev,
      [serviceType]: prev[serviceType].map((s) =>
        s.id === id ? { ...s, basePrice: value } : s
      ),
    }));
  };

  const handleSave = () => {
    onUpdate(edited);
    alert("تم حفظ الأسعار بنجاح!");
  };

  if (!authenticated) {
    return (
      <div className="admin-login">
        <form onSubmit={handleLogin}>
          <h2>تسجيل دخول الإدارة</h2>
          <input
            type="password"
            placeholder="كلمة المرور"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button type="submit">دخول</button>
        </form>
      </div>
    );
  }

  return (
    <div className="admin-services">
      <div className="admin-tabs">
        {TABS.map((t) => (
          <button
            key={t.key}
            className={tab === t.key ? "active" : ""}
            onClick={() => setTab(t.key)}
          >
            {t.label}
          </button>
        ))}
      </div>
      <h2>تعديل أسعار {TABS.find((t) => t.key === tab).label}</h2>
      <table>
        <thead>
          <tr>
            <th>الخدمة</th>
            <th>السعر الأساسي (جنيه)</th>
          </tr>
        </thead>
        <tbody>
          {edited[tab]?.map((service) => (
            <tr key={service.id}>
              <td>{service.name}</td>
              <td>
                <input
                  type="number"
                  value={service.basePrice}
                  onChange={(e) => handleChange(tab, service.id, Number(e.target.value))}
                  min={0}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button className="save-btn" onClick={handleSave}>
        حفظ التعديلات
      </button>
    </div>
  );
};

export default AdminServices;
