import React, { useState } from "react";
import "./AdminServices.css";

const PASSWORD = "2%txPg6DXN";

const AdminServices = ({ services, onUpdate }) => {
  const [password, setPassword] = useState("");
  const [authenticated, setAuthenticated] = useState(false);
  const [editedServices, setEditedServices] = useState(services);

  const handleLogin = (e) => {
    e.preventDefault();
    if (password === PASSWORD) setAuthenticated(true);
    else alert("كلمة المرور غير صحيحة");
  };

  const handleChange = (id, value) => {
    setEditedServices(
      editedServices.map((s) =>
        s.id === id ? { ...s, basePrice: value } : s
      )
    );
  };

  const handleSave = () => {
    onUpdate(editedServices);
    alert("تم حفظ الأسعار بنجاح!");
  };

  if (!authenticated) {
    return (
      <div className="admin-login">
        <form onSubmit={handleLogin}>
          <h2>تسجيل دخول الإدارة</h2>
          <input
            type="password"
            placeholder="كلمة المرور"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button type="submit">دخول</button>
        </form>
      </div>
    );
  }

  return (
    <div className="admin-services">
      <h2>تعديل أسعار خدمات التمريض</h2>
      <table>
        <thead>
          <tr>
            <th>الخدمة</th>
            <th>السعر الأساسي (جنيه)</th>
          </tr>
        </thead>
        <tbody>
          {editedServices.map((service) => (
            <tr key={service.id}>
              <td>{service.name}</td>
              <td>
                <input
                  type="number"
                  value={service.basePrice}
                  onChange={(e) => handleChange(service.id, Number(e.target.value))}
                  min={0}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button className="save-btn" onClick={handleSave}>
        حفظ التعديلات
      </button>
    </div>
  );
};

export default AdminServices;
