import React, { useState, useEffect } from "react";
import "./OrderNow.css";

const providerTypes = [
  { key: "doctor", label: "طبيب", icon: "/doctor-icon.png", color: "#3182ce" },
  { key: "nurse", label: "ممرض", icon: "/nurse-icon.png", color: "#38b2ac" },
  { key: "physio", label: "علاج طبيعي", icon: "/pharmacist-icon.png", color: "#f6ad55" },
  { key: "lab", label: "تحاليل", icon: "/pharmacist-icon.png", color: "#f56565" },
  { key: "xray", label: "أشعة", icon: "/pharmacist-icon.png", color: "#805ad5" },
  { key: "pharmacist", label: "صيدلي", icon: "/pharmacist-icon.png", color: "#ecc94b" },
];

export default function OrderNow({ user }) {
  const [step, setStep] = useState(0);
  const [selectedType, setSelectedType] = useState(null);
  const [specialties, setSpecialties] = useState([]);
  const [selectedSpecialty, setSelectedSpecialty] = useState("");
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState("");
  const [location, setLocation] = useState({ address: "", lat: "", lng: "" });
  const [basePrice, setBasePrice] = useState(0);
  const [suggestedPrice, setSuggestedPrice] = useState("");
  const [sending, setSending] = useState(false);

  // جلب التخصصات من db.json
  useEffect(() => {
    if (selectedType) {
      fetch(`http://localhost:5000/services?providerType=${selectedType.key}`)
        .then(res => res.json())
        .then(data => {
          // استخرج التخصصات الفريدة
          const specialties = [...new Set(data.map(s => s.specialty))];
          setSpecialties(specialties);
        });
    }
  }, [selectedType]);

  // جلب الخدمات حسب نوع مقدم الخدمة (ممرض)
  useEffect(() => {
    if (selectedType && selectedType.key === "nurse") {
      fetch("/db.json")
        .then(res => res.json())
        .then(data => setServices(data.nurseServices || []));
    }
  }, [selectedType]);

  // جلب السعر الأساسي
  useEffect(() => {
    if (selectedType && selectedType.key === "nurse" && selectedService) {
      const service = services.find(s => s.name === selectedService);
      setBasePrice(service ? service.basePrice : 0);
    }
  }, [selectedService, services, selectedType]);

  // تحديد الموقع تلقائياً
  function detectLocation() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(pos => {
        setLocation({
          ...location,
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      });
    }
  }

  // إرسال الطلب
  function handleSubmit(e) {
    e.preventDefault();
    setSending(true);
    fetch("http://localhost:5000/orders", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        patientId: user.id,
        providerType: selectedType.key,
        specialty: selectedSpecialty,
        serviceName: selectedService,
        location,
        basePrice,
        suggestedPrice,
        status: "new",
        createdAt: new Date().toISOString()
      }),
    })
      .then(() => setSending(false));
  }

  // واجهة اختيار التخصص أو الخدمة
  if (step === 0) {
    return (
      <div className="order-cards-container">
        <h2>اختر نوع مقدم الخدمة</h2>
        <div className="cards-list">
          {providerTypes.map((type) => (
            <div
              key={type.key}
              className="order-card"
              style={{ background: type.color }}
              onClick={() => {
                setSelectedType(type);
                if (type.key === "nurse") setStep(1);
                else setStep(2); // باقي التخصصات
              }}
            >
              <img src={type.icon} alt={type.label} className="order-card-icon" />
              <div>{type.label}</div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // واجهة اختيار الخدمة للممرض
  if (step === 1 && selectedType && selectedType.key === "nurse") {
    return (
      <div className="order-step">
        <h2>اختر الخدمة المطلوبة</h2>
        <div className="services-list-grid">
          {services.map(s => (
            <div
              key={s.id}
              className={`service-card${selectedService === s.name ? " selected" : ""}`}
              onClick={() => { setSelectedService(s.name); setBasePrice(s.basePrice); }}
            >
              <div className="service-name">{s.name}</div>
              <div className="service-price">{s.basePrice} ج.م</div>
            </div>
          ))}
        </div>
        <button onClick={() => setStep(3)} disabled={!selectedService}>التالي</button>
      </div>
    );
  }

  // واجهة الموقع والسعر
  if (step === 3 && selectedType && selectedType.key === "nurse") {
    return (
      <form className="order-step" onSubmit={handleSubmit}>
        <h2>حدد موقعك</h2>
        <input
          type="text"
          placeholder="العنوان"
          value={location.address}
          onChange={e => setLocation({ ...location, address: e.target.value })}
        />
        <button type="button" onClick={detectLocation}>تحديد الموقع تلقائياً</button>
        <div className="price-box">
          <b>سعر الخدمة الأساسي:</b> <span className="main-price">{basePrice} ج.م</span>
        </div>
        <input
          type="number"
          placeholder="اقتراح سعر (اختياري)"
          value={suggestedPrice}
          onChange={e => setSuggestedPrice(e.target.value)}
        />
        <button type="submit" disabled={sending}>{sending ? "جاري الإرسال..." : "إرسال الطلب"}</button>
      </form>
    );
  }

  return null;
}
