import React, { useEffect, useState } from "react";
import MapSection from "./MapSection";
import "./TrackOrder.css";

// دالة لحساب الوقت المقدر للوصول (بناءً على المسافة)
function estimateArrivalTime(lat1, lng1, lat2, lng2) {
  // حساب المسافة بين نقطتين (Haversine)
  function toRad(x) { return x * Math.PI / 180; }
  const R = 6371; // km
  const dLat = toRad(lat2 - lat1);
  const dLon = toRad(lng2 - lng1);
  const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
    Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) *
    Math.sin(dLon/2) * Math.sin(dLon/2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
  const distance = R * c;
  // سرعة افتراضية 40 كم/س
  const speed = 40;
  const time = distance / speed * 60; // بالدقائق
  return Math.round(time);
}

export default function TrackOrder({ orderId = "a435", user }) {
  const [order, setOrder] = useState(null);
  const [status, setStatus] = useState("جاري تجهيز الطلب...");
  const [showPopup, setShowPopup] = useState(false);
  const [providerInfo, setProviderInfo] = useState(null);
  const [arrivalTime, setArrivalTime] = useState(null);

  useEffect(() => {
    // جلب بيانات الطلب من قاعدة البيانات
    fetch("/db.json")
      .then(res => res.json())
      .then(data => {
        const found = (data.orders || []).find(o => o.id === orderId);
        setOrder(found);
        if (found) setStatus(found.status || "جاري تجهيز الطلب...");
        // إذا تم قبول الطلب من مقدم الخدمة
        if (found && found.status === "accepted" && found.providerId) {
          // جلب بيانات مقدم الخدمة
          const provider = (data.users || []).find(u => u.id === found.providerId);
          setProviderInfo(provider);
          // حساب الوقت المقدر للوصول
          if (provider && found.location && provider.location) {
            const t = estimateArrivalTime(
              found.location.lat, found.location.lng,
              provider.location.lat, provider.location.lng
            );
            setArrivalTime(t);
          }
          setShowPopup(true);
        }
      });
  }, [orderId]);

  return (
    <div className="track-main">
      <h2>متابعة الطلب</h2>
      <div className="track-status">{status}</div>
      {order && (
        <div className="order-prices-box">
          <div><b>سعر الخدمة الأساسي:</b> <span className="main-price">{order.basePrice} ج.م</span></div>
          {order.suggestedPrice && order.suggestedPrice !== order.basePrice?.toString() && (
            <div><b>السعر المقترح من المريض:</b> <span className="suggested-price">{order.suggestedPrice} ج.م</span></div>
          )}
        </div>
      )}
      <div className="track-map">
        <MapSection location={order?.location || {lat: 30.0444, lng: 31.2357}} searching={false} />
      </div>
      {/* Popup عند قبول الطلب */}
      {showPopup && providerInfo && (
        <div className="arrival-popup-modern">
          <div className="popup-title">مقدم الخدمة في الطريق إليك!</div>
          <div className="popup-content">
            <div><b>الاسم:</b> {providerInfo.fullName}</div>
            <div><b>رقم الهاتف:</b> {providerInfo.phone}</div>
            <div><b>الوقت المقدر للوصول:</b> {arrivalTime} دقيقة</div>
          </div>
          <button className="close-popup-btn" onClick={() => setShowPopup(false)}>إغلاق</button>
        </div>
      )}
    </div>
  );
}
