import React, { useState, useEffect } from "react";
import "./OrderNow.css";

const providerTypes = [
  { key: "doctor", label: "طبيب", icon: "/doctor-icon.png", color: "#3182ce" },
  { key: "nurse", label: "ممرض", icon: "/nurse-icon.png", color: "#38b2ac" },
  { key: "physio", label: "علاج طبيعي", icon: "/pharmacist-icon.png", color: "#f6ad55" },
  { key: "lab", label: "تحاليل", icon: "/pharmacist-icon.png", color: "#f56565" },
  { key: "xray", label: "أشعة", icon: "/pharmacist-icon.png", color: "#805ad5" },
  { key: "pharmacist", label: "صيدلي", icon: "/pharmacist-icon.png", color: "#ecc94b" },
];

export default function OrderNow({ user }) {
  const [step, setStep] = useState(0);
  const [selectedType, setSelectedType] = useState(null);
  const [specialties, setSpecialties] = useState([]);
  const [selectedSpecialty, setSelectedSpecialty] = useState("");
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState("");
  const [location, setLocation] = useState({ address: "", lat: "", lng: "" });
  const [basePrice, setBasePrice] = useState(0);
  const [suggestedPrice, setSuggestedPrice] = useState("");
  const [sending, setSending] = useState(false);

  // جلب التخصصات من الباك اند
  useEffect(() => {
    if (selectedType) {
      fetch(`/api/services?providerType=${selectedType.key}`)
        .then(res => res.json())
        .then(data => setSpecialties(data.specialties));
    }
  }, [selectedType]);

  // جلب الخدمات حسب التخصص
  useEffect(() => {
    if (selectedSpecialty) {
      fetch(`/api/services?providerType=${selectedType.key}&specialty=${selectedSpecialty}`)
        .then(res => res.json())
        .then(data => setServices(data.services));
    }
  }, [selectedSpecialty]);

  // جلب السعر الأساسي
  useEffect(() => {
    if (selectedService) {
      fetch(`/api/services/price?serviceName=${selectedService}`)
        .then(res => res.json())
        .then(data => setBasePrice(data.basePrice));
    }
  }, [selectedService]);

  // تحديد الموقع تلقائياً
  function detectLocation() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(pos => {
        setLocation({
          ...location,
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      });
    }
  }

  // إرسال الطلب
  function handleSubmit(e) {
    e.preventDefault();
    setSending(true);
    fetch("/api/orders", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        patientId: user._id,
        providerType: selectedType.label,
        specialty: selectedSpecialty,
        serviceName: selectedService,
        location,
        basePrice,
        suggestedPrice,
      }),
    })
      .then(() => setSending(false));
  }

  // واجهة اختيار التخصص
  if (step === 0) {
    return (
      <div className="order-cards-container">
        <h2>اختر نوع مقدم الخدمة</h2>
        <div className="cards-list">
          {providerTypes.map((type) => (
            <div
              key={type.key}
              className="order-card"
              style={{ background: type.color }}
              onClick={() => { setSelectedType(type); setStep(1); }}
            >
              <img src={type.icon} alt={type.label} className="order-card-icon" />
              <div>{type.label}</div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // واجهة اختيار التخصص الفرعي
  if (step === 1) {
    return (
      <div className="order-step">
        <h2>اختر التخصص</h2>
        <select value={selectedSpecialty} onChange={e => setSelectedSpecialty(e.target.value)}>
          <option value="">اختر التخصص...</option>
          {specialties.map(sp => <option key={sp} value={sp}>{sp}</option>)}
        </select>
        <button onClick={() => setStep(2)} disabled={!selectedSpecialty}>التالي</button>
      </div>
    );
  }

  // واجهة اختيار الخدمة
  if (step === 2) {
    return (
      <div className="order-step">
        <h2>اختر الخدمة المطلوبة</h2>
        <select value={selectedService} onChange={e => setSelectedService(e.target.value)}>
          <option value="">اختر الخدمة...</option>
          {services.map(s => <option key={s.name} value={s.name}>{s.name}</option>)}
        </select>
        <button onClick={() => setStep(3)} disabled={!selectedService}>التالي</button>
      </div>
    );
  }

  // واجهة الموقع والسعر
  if (step === 3) {
    return (
      <form className="order-step" onSubmit={handleSubmit}>
        <h2>حدد موقعك</h2>
        <input
          type="text"
          placeholder="العنوان"
          value={location.address}
          onChange={e => setLocation({ ...location, address: e.target.value })}
        />
        <button type="button" onClick={detectLocation}>تحديد الموقع تلقائياً</button>
        <div>
          <b>سعر الخدمة الأساسي:</b> {basePrice} ج.م
        </div>
        <input
          type="number"
          placeholder="اقتراح سعر (اختياري)"
          value={suggestedPrice}
          onChange={e => setSuggestedPrice(e.target.value)}
        />
        <button type="submit" disabled={sending}>{sending ? "جاري الإرسال..." : "إرسال الطلب"}</button>
      </form>
    );
  }

  return null;
}
